import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    subject: '',
    email: '',
  });

  const [submittedData, setSubmittedData] = useState([]);
  const [isDataLoaded, setIsDataLoaded] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post('http://localhost:4000/submit-form', formData)
      .then((response) => {
        console.log(response.data);
        // Handle success, e.g., show the data in a table
        fetchSubmittedData(); // Fetch and update the submitted data
      })
      .catch((error) => {
        console.error(error);
        // Handle error
      });
  };

  const fetchSubmittedData = () => {
    axios.get('http://localhost:4000/fetch-data') // Create a new API endpoint to fetch data from the backend
      .then((response) => {
        setSubmittedData(response.data);
        setIsDataLoaded(true);
      })
      .catch((error) => {
        console.error(error);
        // Handle error
      });
  };

  useEffect(() => {
    fetchSubmittedData();
  }, []);

  return (
    <div>
      <h1>Form Submission</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} />
        <input type="text" name="subject" placeholder="Subject" value={formData.subject} onChange={handleChange} />
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} />
        <button type="submit">Submit</button>
      </form>

      {isDataLoaded && (
        <div>
          <h2>Submitted Data</h2>
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Subject</th>
                <th>Email</th>
              </tr>
            </thead>
            <tbody>
              {submittedData.map((data) => (
                <tr key={data.id}>
                  <td>{data.name}</td>
                  <td>{data.subject}</td>
                  <td>{data.email}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
